import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { quizData } from '../QuizData/QuizData';
import { Typography, Button, Container, Grid, Paper } from '@mui/material';

const QuizPage = () => {
  const { topic, difficulty, quizIndex } = useParams();
  const quiz = quizData[topic][difficulty][quizIndex];
  const [answers, setAnswers] = useState(new Array(quiz.questions.length).fill('')); // Array to store user answers
  const [currentIndex, setCurrentIndex] = useState(0);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes (300 seconds) timer
  const [selectedOption, setSelectedOption] = useState('');
  const [correctness, setCorrectness] = useState(new Array(quiz.questions.length).fill(null)); // Track if the selected option is correct

  useEffect(() => {
    const timer = setTimeout(() => {
      if (timeLeft > 0) {
        setTimeLeft(timeLeft - 1);
      }
    }, 1000);

    return () => clearTimeout(timer);
  }, [timeLeft]);

  const handleOptionSelect = (option) => {
    const newAnswers = [...answers];
    newAnswers[currentIndex] = option;
    setAnswers(newAnswers);

    const isCorrect = option === quiz.questions[currentIndex].answer;
    const newCorrectness = [...correctness];
    newCorrectness[currentIndex] = isCorrect;
    setCorrectness(newCorrectness);
    setSelectedOption(option);
  };

  const handleSubmit = () => {
    // Calculate score or perform other actions
    console.log('Submitted Answers:', answers);
    // You can navigate to a result page or display the score here
  };

  const renderOptions = () => {
    return (
      <Grid container spacing={2} direction="column">
        <Grid item>
          <Typography variant="h6">{quiz.questions[currentIndex].question}</Typography>
        </Grid>
        {quiz.questions[currentIndex].options.map((option, idx) => (
          <Grid item key={idx}>
            <Button
              variant="contained"
              onClick={() => handleOptionSelect(option)}
              style={{
                backgroundColor:
                  selectedOption === option
                    ? option === quiz.questions[currentIndex].answer
                      ? 'green'
                      : 'red'
                    : 'default',
                color: selectedOption === option ? 'white' : 'default',
              }}
            >
              {option}
            </Button>
          </Grid>
        ))}
      </Grid>
    );
  };

  return (
    <Container maxWidth="sm" style={{ marginTop: '2rem' }}>
      <Paper style={{ padding: '2rem', marginBottom: '2rem' }}>
        <Typography variant="h4" align="center" gutterBottom>{quiz.title}</Typography>
        <Typography variant="body1" align="center" gutterBottom>Time Left: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toLocaleString('en-US', { minimumIntegerDigits: 2 })}</Typography>
        <div style={{ marginBottom: '2rem' }}>
          {renderOptions()}
        </div>
        <Grid container justifyContent="center">
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setCurrentIndex(currentIndex + 1);
              setSelectedOption(''); // Reset selected option for the next question
            }}
            disabled={currentIndex === quiz.questions.length - 1}
          >
            {currentIndex === quiz.questions.length - 1 ? 'Submit' : 'Next Question'}
          </Button>
        </Grid>
      </Paper>
      {currentIndex === quiz.questions.length && (
        <Paper style={{ padding: '2rem' }}>
          <Typography variant="h5" align="center" gutterBottom>Quiz Completed!</Typography>
          {/* Render result or score here */}
          <Button variant="contained" color="primary" onClick={handleSubmit}>Submit Answers</Button>
        </Paper>
      )}
    </Container>
  );
};

export default QuizPage;
