import React, { useState } from 'react';
import { Box, Typography, Button, Divider, Grid, Modal } from '@mui/material';

const QuizApp = () => {
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [selectedDifficulty, setSelectedDifficulty] = useState(null);
  const [openModal, setOpenModal] = useState(false);

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
  };

  const handleDifficultySelect = (difficulty) => {
    setSelectedDifficulty(difficulty);
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
  };

  const renderOptions = () => {
    if (selectedTopic === null) {
      return null;
    }

    return (
      <Grid container spacing={2} direction="column" alignItems="center">
        <Grid item>
          <Typography variant="h5">Choose Difficulty:</Typography>
        </Grid>
        {['Beginner', 'Intermediate', 'Advanced'].map((difficulty, index) => (
          <Grid item key={index}>
            <Button variant="contained" color="primary" onClick={() => handleDifficultySelect(difficulty)}>
              {selectedTopic} {difficulty}
            </Button>
          </Grid>
        ))}
      </Grid>
    );
  };

  const renderModalContent = () => (
    <Box sx={{ bgcolor: 'background.paper', p: 4, borderRadius: 1, boxShadow: 24 }}>
      <Typography variant="h6" component="h2">
        {selectedTopic} {selectedDifficulty}
      </Typography>
      <Typography sx={{ mt: 2 }}>Choose a Quiz:</Typography>
      <Grid container spacing={2} sx={{ mt: 2 }}>
        {[1, 2, 3, 4, 5].map((quiz) => (
          <Grid item key={quiz} xs={12}>
            <Button variant="outlined" fullWidth onClick={() => console.log(`Selected Quiz: Quiz ${quiz}`)}>
              Quiz {quiz}
            </Button>
          </Grid>
        ))}
      </Grid>
      <Button variant="contained" color="secondary" onClick={handleCloseModal} sx={{ mt: 2 }}>
        Close
      </Button>
    </Box>
  );

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', p: 2 }}>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, width: '100%', maxWidth: 600 }}>
        <Typography variant="h5" textAlign="center">Choose a Topic:</Typography>
        <Grid container spacing={2} justifyContent="center">
          {['HTML', 'CSS', 'JavaScript', 'React'].map((topic, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <Button
                variant="contained"
                color="primary"
                fullWidth
                onClick={() => handleTopicSelect(topic)}
              >
                {topic}
              </Button>
            </Grid>
          ))}
        </Grid>
        <Divider />
        {renderOptions()}
      </Box>
      <Modal
        open={openModal}
        onClose={handleCloseModal}
        aria-labelledby="quiz-modal-title"
        aria-describedby="quiz-modal-description"
      >
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: 400,
            bgcolor: 'background.paper',
            boxShadow: 24,
            p: 4,
            borderRadius: 1,
          }}
        >
          {renderModalContent()}
        </Box>
      </Modal>
    </Box>
  );
};

export default QuizApp;
