import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { quizData } from '../QuizData/QuizData';

const QuizPage = () => {
  const { topic, difficulty, quizIndex } = useParams();
  const quiz = quizData[topic][difficulty][quizIndex];
  const [answers, setAnswers] = useState(new Array(quiz.questions.length).fill('')); // Array to store user answers
  const [currentIndex, setCurrentIndex] = useState(0);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes (300 seconds) timer
  const [selectedOptions, setSelectedOptions] = useState(new Array(quiz.questions.length).fill('')); // Track selected option for each question

  useEffect(() => {
    const timer = setTimeout(() => {
      if (timeLeft > 0) {
        setTimeLeft(timeLeft - 1);
      }
    }, 1000);

    return () => clearTimeout(timer);
  }, [timeLeft]);

  const handleOptionSelect = (option) => {
    const newAnswers = [...answers];
    newAnswers[currentIndex] = option;
    setAnswers(newAnswers);

    const newSelectedOptions = [...selectedOptions];
    newSelectedOptions[currentIndex] = option;
    setSelectedOptions(newSelectedOptions);
  };

  const handleSubmit = () => {
    // Calculate score or perform other actions
    console.log('Submitted Answers:', answers);
    // You can navigate to a result page or display the score here
  };

  const renderOptions = () => {
    return (
      <div>
        <p>{quiz.questions[currentIndex].question}</p>
        {quiz.questions[currentIndex].options.map((option, idx) => (
          <button
            key={idx}
            onClick={() => handleOptionSelect(option)}
            style={{
              backgroundColor:
                selectedOptions[currentIndex] === option
                  ? option === quiz.questions[currentIndex].answer
                    ? 'green'
                    : 'red'
                  : 'white',
              color: selectedOptions[currentIndex] === option ? 'white' : 'black',
              margin: '5px',
              padding: '10px',
            }}
          >
            {option}
          </button>
        ))}
      </div>
    );
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <div style={{ padding: '20px', marginBottom: '20px', border: '1px solid #ccc' }}>
        <h2 style={{ textAlign: 'center' }}>{quiz.title}</h2>
        <p style={{ textAlign: 'center' }}>Time Left: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toLocaleString('en-US', { minimumIntegerDigits: 2 })}</p>
        <div style={{ marginBottom: '20px' }}>
          {renderOptions()}
        </div>
        <div style={{ textAlign: 'center' }}>
          {currentIndex < quiz.questions.length - 1 ? (
            <button
              onClick={() => setCurrentIndex(currentIndex + 1)}
              style={{ padding: '10px 20px', margin: '5px' }}
            >
              Next Question
            </button>
          ) : (
            <button
              onClick={handleSubmit}
              style={{ padding: '10px 20px', margin: '5px' }}
            >
              Submit
            </button>
          )}
        </div>
      </div>
      {currentIndex === quiz.questions.length && (
        <div style={{ padding: '20px', border: '1px solid #ccc' }}>
          <h3 style={{ textAlign: 'center' }}>Quiz Completed!</h3>
          {/* Render result or score here */}
          <button
            onClick={handleSubmit}
            style={{ padding: '10px 20px', margin: '5px', display: 'block', marginLeft: 'auto', marginRight: 'auto' }}
          >
            Submit Answers
          </button>
        </div>
      )}
    </div>
  );
};

export default QuizPage;
