import React, { useState } from 'react';
import { Box, Typography, Button, Divider } from '@mui/material';

const QuizApp = () => {
  const [selectedTopic, setSelectedTopic] = useState(null);

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
  };

  const handleDifficultySelect = (difficulty) => {
    console.log(`Selected Topic: ${selectedTopic}, Difficulty: ${difficulty}`);
    // Here you can navigate to the quiz page or perform any other action based on the selected topic and difficulty
  };

  const renderOptions = () => {
    if (selectedTopic === null) {
      return null;
    }

    return (
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <Typography variant="h5">Choose Difficulty:</Typography>
        <Button variant="contained" color="primary" onClick={() => handleDifficultySelect('Beginner')}>
          Beginner
        </Button>
        <Button variant="contained" color="primary" onClick={() => handleDifficultySelect('Intermediate')}>
          Intermediate
        </Button>
        <Button variant="contained" color="primary" onClick={() => handleDifficultySelect('Advanced')}>
          Advanced
        </Button>
      </Box>
    );
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <Typography variant="h5">Choose a Topic:</Typography>
        <Button variant="contained" color="primary" onClick={() => handleTopicSelect('HTML')}>
          HTML
        </Button>
        <Button variant="contained" color="primary" onClick={() => handleTopicSelect('CSS')}>
          CSS
        </Button>
        <Button variant="contained" color="primary" onClick={() => handleTopicSelect('JS')}>
          JavaScript
        </Button>
        <Button variant="contained" color="primary" onClick={() => handleTopicSelect('React')}>
          React
        </Button>
        <Divider />
        {renderOptions()}
      </Box>
    </Box>
  );
};

export default QuizApp;
